import React, { useState } from 'react';
import { View, Text, TouchableOpacity, Image, Modal, StyleSheet } from 'react-native';
import { FontAwesome } from '@expo/vector-icons';

const Students = ({ navigation }) => {
  const [isDenunciasModalVisible, setDenunciasModalVisible] = useState(false);

  const toggleDenunciasModal = () => {
    setDenunciasModalVisible(!isDenunciasModalVisible);
  };

  return (
    <View style={styles.container}>
      <Image
        source={require('../images/municipioqueretaro.png')}
        style={styles.image}
      />
      <Text style={styles.title}>Bienvenido a la página para estudiantes</Text>

      {/* Botón de Denuncias */}
      <TouchableOpacity style={styles.button} onPress={toggleDenunciasModal}>
        <FontAwesome name="exclamation-triangle" size={30} color="white" />
        <Text style={styles.buttonText}>Denuncias</Text>
      </TouchableOpacity>

      {/* Modal de Denuncias */}
      <Modal
        animationType="slide"
        transparent={true}
        visible={isDenunciasModalVisible}
        onRequestClose={toggleDenunciasModal}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>Categorías de Denuncias</Text>
            <TouchableOpacity onPress={() => alert('Denuncias Penales')}>
              <Text style={styles.modalItem}>Denuncias Penales: Relacionadas con actividades delictivas. Pueden incluir delitos como robo, fraude, agresión, etc.</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => alert('Denuncias Civiles')}>
              <Text style={styles.modalItem}>Denuncias Civiles: Se presentan en casos de disputas entre individuos, como casos de negligencia, disputas contractuales, daños y perjuicios, entre otros.</Text>
            </TouchableOpacity>
            {/* Agrega más opciones de denuncias aquí */}
            <TouchableOpacity onPress={toggleDenunciasModal}>
              <Text style={styles.closeButton}>Cerrar</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>

      <TouchableOpacity
        style={styles.button}
        onPress={() => alert('Implementa la lógica de cierre de sesión')}
      >
        <FontAwesome name="sign-out" size={30} color="white" />
        <Text style={styles.buttonText}>Cerrar Sesión</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  image: {
    width: 200,
    height: 200,
    resizeMode: 'contain',
    marginBottom: 20,
  },
  button: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#0073e6',
    padding: 10,
    borderRadius: 10,
    marginBottom: 10,
  },
  buttonText: {
    color: 'white',
    fontSize: 20,
    marginLeft: 10,
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  modalContent: {
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 10,
    width: '80%',
  },
  modalTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  modalItem: {
    fontSize: 16,
    marginBottom: 10,
  },
  closeButton: {
    fontSize: 18,
    color: 'blue',
    textAlign: 'center',
    marginTop: 20,
  },
});

export default Students;
