import React from 'react';
import { View, Text, TouchableOpacity, Image, StyleSheet } from 'react-native';
import { FontAwesome } from '@expo/vector-icons'; // Importa el icono FontAwesome (puedes utilizar otras bibliotecas de iconos)

const Students = ({ navigation }) => {
  const handleDenuncia = () => {
    // Lógica para la opción "Denunciar"
    // Puedes navegar a una nueva pantalla o mostrar un modal aquí
    alert('Denunciar');
  };

  const renderDenunciaSubMenu = () => {
    // Submenú de "Denuncias"
    return (
      <View style={styles.subMenuContainer}>
        <TouchableOpacity style={styles.subMenuItem}>
          <FontAwesome name="exclamation-triangle" size={24} color="white" />
          <Text style={styles.subMenuItemText}>Denuncias penales</Text>
          <Text style={styles.subMenuItemDescription}>
            Relacionadas con actividades delictivas. Pueden incluir delitos como robo, fraude, agresión, etc.
          </Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.subMenuItem}>
          <FontAwesome name="exclamation-triangle" size={24} color="white" />
          <Text style={styles.subMenuItemText}>Denuncias civiles</Text>
          <Text style={styles.subMenuItemDescription}>
            Se presentan en casos de disputas entre individuos, como casos de negligencia, disputas contractuales, daños y perjuicios, entre otros.
          </Text>
        </TouchableOpacity>

        {/* Agrega más opciones de denuncias aquí */}
      </View>
    );
  };

  return (
    <View style={styles.container}>
      <Image
        source={require('../images/municipioqueretaro.png')}
        style={styles.image}
      />
      <Text style={styles.title}>Bienvenido a la página para estudiantes</Text>

      {/* Botón de "Denunciar" */}
      <TouchableOpacity style={styles.button} onPress={handleDenuncia}>
        <FontAwesome name="exclamation-circle" size={30} color="white" />
        <Text style={styles.buttonText}>Denunciar</Text>
      </TouchableOpacity>

      {/* Muestra el submenú de "Denuncias" al hacer clic en "Denunciar" */}
      {renderDenunciaSubMenu()}

      <TouchableOpacity
        style={styles.button}
        onPress={() => alert('Implementa la lógica de cierre de sesión')}
      >
        <FontAwesome name="sign-out" size={30} color="white" />
        <Text style={styles.buttonText}>Cerrar Sesión</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  image: {
    width: 200,
    height: 200,
    resizeMode: 'contain',
    marginBottom: 20,
  },
  button: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#0073e6',
    padding: 10,
    borderRadius: 10,
    marginBottom: 10,
  },
  buttonText: {
    color: 'white',
    fontSize: 20,
    marginLeft: 10,
  },
  subMenuContainer: {
    marginTop: 10,
  },
  subMenuItem: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#0073e6',
    padding: 10,
    borderRadius: 10,
    marginBottom: 10,
  },
  subMenuItemText: {
    color: 'white',
    fontSize: 20,
    marginLeft: 10,
  },
  subMenuItemDescription: {
    color: 'white',
    fontSize: 14,
    marginLeft: 10,
  },
});

export default Students;
